// GSAP PRELOADER

function preLoader() {
    const preLoader = document.querySelector(".preLoader");
    if (!preLoader) return;
    let counts = setInterval(updated, 4);
    let upto = 0;

    function updated() {
        let count = document.querySelector(".preLoader .counter span");
        count.innerHTML = ++upto;
        if (upto === 100) {
            clearInterval(counts);
            startAnimation();
        }
    }

    function startAnimation() {
        const tl = gsap.timeline({
            duration: 0.1,
        });
        tl.to(".preLoader .counter", {
                autoAlpha: 0,
            })
            .to(".preLoader .line", {
                width: "100%",
                duration: 0.5,
            }) // Line animation starts after the counter ends
            .to(".preLoader .line", {
                xPercent: 100,
            })
            .to(".preLoader .bar", {
                height: 0,
                stagger: 0.09,
                ease: "power4.inOut",
            })
            .to(".preLoader", {
                css: {
                    display: "none",
                },
            })
            .from(
                ".top-user, .header-logo, .top-social", {
                    y: -50,
                    autoAlpha: 0,
                    stagger: 0.1,
                },
                "-=0.5"
            )
            .from(
                ".navbar-nav > li, .contactWrap > div", {
                    autoAlpha: 0,
                    x: -100,
                    stagger: 0.05,
                },
                "-=0.375"
            )
            .from(".main-slider", {
                autoAlpha: 0,
                stagger: 0.1,
            })
            .from(".slideOne", {
                y: 50,
                autoAlpha: 0,
                stagger: 0.1,
            });

        document.querySelector("body").classList.remove("loading");
    }
}

//  CUSTOM MOUSE CURSOR

function mouseCursor() {
    if (document.querySelector(".cursor-inner") || document.querySelector(".cursor-outer")) {
        const cursorInner = document.querySelector(".cursor-inner");
        const cursorOuter = document.querySelector(".cursor-outer");
        let mouseX = 0;
        let mouseY = 0;
        let isHovering = false;

        window.addEventListener("mousemove", function (event) {
            if (!isHovering) {
                gsap.to(cursorOuter, {
                    x: event.clientX,
                    y: event.clientY,
                });
            }
            gsap.to(cursorInner, {
                x: event.clientX,
                y: event.clientY,
            });
            mouseY = event.clientY;
            mouseX = event.clientX;
        });

        const elementsToTrack = document.querySelectorAll("a, img, .cursor-pointer");
        elementsToTrack.forEach((element) => {
            element.addEventListener("mouseenter", function () {
                cursorInner.classList.add("cursor-hover");
                cursorOuter.classList.add("cursor-hover");
                isHovering = true;
            });

            element.addEventListener("mouseleave", function () {
                if (!(
                        element.tagName === "A" ||
                        element.tagName === "IMG" ||
                        element.closest(".cursor-pointer")
                    )) {
                    cursorInner.classList.remove("cursor-hover");
                    cursorOuter.classList.remove("cursor-hover");
                }
                isHovering = false;
            });
        });

        gsap.set(cursorInner, {
            visibility: "visible"
        });
        gsap.set(cursorOuter, {
            visibility: "visible"
        });
    }
}

// ANCHOR VOID MANIPULATE #

function replaceHash() {
    document.querySelectorAll("a").forEach((a) => {
        let href = a.getAttribute("href");
        a.href = href ?
            href.startsWith("#") && href.endsWith("#") ?
            href.replace("#", "javascript:;") :
            href :
            "javascript:;";
    });
}

// GSAP IMAGE REVEAL ANIMATION

function imgRevealer() {
    let revealContainers = document.querySelectorAll(".reveal");

    revealContainers.forEach((container) => {
        let image = container.querySelector("img");
        let revelerTl = gsap.timeline({
            scrollTrigger: {
                // scroller: '[data-scroll-container]',
                trigger: container,
                toggleActions: "restart none none reverse",
            },
        });

        revelerTl.set(container, {
            autoAlpha: 1
        });
        revelerTl.from(container, 0.75, {
            xPercent: -100,
            ease: Power2.out,
        });
        revelerTl.from(image, 0.75, {
            xPercent: 100,
            scale: 1.3,
            delay: -0.75,
            ease: Power2.out,
        });
    });
}

// GSAP HEADING ANIMATION

function headingAnimation() {
    const headings = document.querySelectorAll(".mainHead");
    const subHeading = document.querySelectorAll(".subHead");

    headings.forEach((text) => {
        let text1 = new SplitType(text, {
            types: "chars",
        });
        const headingTl = gsap.timeline({
            scrollTrigger: {
                trigger: text,
                toggleActions: "play none none reset",
            },
        });
        let chars = text.querySelectorAll(".char");
        headingTl.from(chars, 1.2, {
            x: 80,
            opacity: 0,
            scale: 1.2,
            rotationX: -90,
            transformOrigin: "bottom center",
            stagger: 0.05,
            ease: "expo.out",
        });
    });

    subHeading.forEach((text) => {
        let text1 = new SplitType(text, {
            types: "chars",
        });
        const subHeadingTl = gsap.timeline({
            scrollTrigger: {
                trigger: text,
                toggleActions: "play none none reset",
            },
        });
        let chars = text.querySelectorAll(".char");
        subHeadingTl.from(chars, 1.2, {
            y: -80, // Changed to negative value
            opacity: 0,
            scale: 1.2,
            rotationX: 90, // Adjusted to rotate from the top
            transformOrigin: "top center", // Changed to match the new direction
            stagger: 0.1,
            ease: "expo.out",
        });
    });
}

// LENIS SETUP

function lenisSetup() {
    const lenis = new Lenis({
        duration: 1.5,
        easing: (t) => Math.min(1, 1.001 - Math.pow(2, -10 * t)),
        smooth: true,
        mouseMultiplier: 1,
    });

    function raf(time) {
        lenis.raf(time);
        requestAnimationFrame(raf);
    }
    requestAnimationFrame(raf);
    document.querySelectorAll('a[href^="#"]').forEach((anchor) => {
        anchor.addEventListener("click", function (e) {
            e.preventDefault();
            lenis.scrollTo(this.getAttribute("href"));
        });
    });
    lenis.scrollTo("top");
}

function btnHover() {
    const buttons = gsap.utils.toArray(".themeBtn");
    buttons.forEach((item) => {
        let text1 = new SplitType(item, {
            types: "chars",
        });
        let chars = item.querySelectorAll(".char");
        let tl = gsap.timeline({
            paused: true,
        });

        tl.to(chars, {
            duration: 0.5,
            yPercent: -200,
            ease: "power2.in",
        });
        tl.set(chars, {
            yPercent: 200,
        });
        tl.to(chars, {
            duration: 0.5,
            yPercent: 0,
            stagger: 0.1,
            ease: "back.inOut(3)",
        });

        item.addEventListener("mouseenter", () => tl.play(0));
    });
}


// Function Initialization
function initializeFunctions() {
    allSliders();
    mouseCursor();
    replaceHash();

    // Animation EFFECTs
    imgRevealer();
    headingAnimation();
    btnHover();

    // LENIS 
    lenisSetup();

    // AOS INIT
    AOS.init({
        duration: 1200,
        disable: 'mobile'
    });
}

$(window).on('load', function () {
    if ($(".preLoader").length > 0) {
        preLoader();
    }
});

// Event listener for window load
window.addEventListener("load", initializeFunctions);